import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component { //represents the entire app
  constructor() {
    super();

    this.state = {
      monsters: [
        {
          name: 'Linda',
          id: 'ertrgg', //use unique id as key - the id is under the hood for react, doesn't show up on html
        },
        {
          name: 'Frank',
          id: 'ertrgg1',
        },
        {
          name: 'Jacky',
          id: 'ertrgg2',
        }
      ]
    };
  }

  render() {
    //include built-in render()method of Component
    return (
      <div className="App">
        {this.state.monsters.map((monster) => {
          return (
            <div key={monster.id}>
              <h1>{monster.name}</h1>
            </div>)
          }) //use array method, e.g. .map() to effectively render multiple obj values
        }
      </div>
    );
  }



}

export default App;
